<script>import useStyles from './Day.styles';
import { getDayTabIndex } from './get-day-tab-index/get-day-tab-index';
import { getDayAutofocus } from './get-day-autofocus/get-day-autofocus';
import { createEventForwarder, useActions } from '@svelteuidev/core';
import { get_current_component } from 'svelte/internal';
export let use = [], element = undefined, className = '', override = {}, value = null, selected = null, weekend = null, outside = null, disabled = null, hasValue = null, inRange = null, firstInRange = null, lastInRange = null, size = null, fullWidth = null, firstInMonth = null, focusable = null, hideOutsideDates = null, primaryColor = 'blue', onMouseEnter = (date, event) => null, renderDay = undefined;
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
$: ({ cx, classes } = useStyles({ size, fullWidth, primaryColor, hideOutsideDates }, { override }));
</script>

<button
	type="button"
	bind:this={element}
	use:useActions={use}
	use:forwardEvents
	on:mouseenter={(event) => onMouseEnter(value, event)}
	tabindex={getDayTabIndex({ focusable, hasValue, selected, firstInMonth })}
	data-autofocus={getDayAutofocus({ hasValue, selected, firstInMonth })}
	data-svelteui-stop-propagation
	class={cx(
		classes.root,
		{
			outside,
			weekend,
			selected,
			inRange,
			firstInRange,
			lastInRange,
			disabled
		},
		className
	)}
	{disabled}
>
	{typeof renderDay === 'function' ? renderDay(value) : value.getDate()}
</button>
